<span class="line"><span style="color:#F8F8F2;">    </span><span style="color:#FF79C6;">public</span><span style="color:#F8F8F2;"> </span><span style="color:#FF79C6;">function</span><span style="color:#F8F8F2;"> </span><span style="color:#50FA7B;">onClose</span><span style="color:#F8F8F2;">(</span><span style="color:#8BE9FD;">ConnectionInterface</span><span style="color:#F8F8F2;"> $connection);</span></span>
